commits.content:
.titulo-pagina h1 {
    margin-bottom: 40px;
}

.single .content img {
    margin-bottom: 10px;
}

section.content {
    padding: 20px 20px 40px;
}

.content .saboxplugin-wrap .saboxplugin-gravatar {
    padding-left: 0;
}

.author-container {
    padding: 30px 0;
}

.single .wpp-thumbnail {
    width: 100px;
    height: auto;
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
    margin-bottom: 0!important;
}

.widget.popular-posts h2 {
    margin-bottom: 26px;
    border-bottom: solid 4px var(--vermelho-escuro);
    padding-bottom: 10px;
}

.wpp-list li {
    margin-bottom: 20px;
    background-color: var(--branco);
    border-radius: 20px;
    line-height: 100px; 
    height: 100px;
    padding-right: 10px;
    display: table;
}

.wpp-post-title {
    line-height: 1.35;
    display: table-cell;
    vertical-align: middle;
    width: 67%;
    text-decoration: none;
}

.titulo-pagina h1 img {
    margin-top: 0px;
}
