commits.content:
/* Topo */

.noticias-topo {
    background: linear-gradient(180deg, #000000 0%, rgba(196, 196, 196, 0) 100%);
    padding: 0;
}

.noticias-topo .banner {
    width: 100%;
    height: 500px;
    margin: 0 auto;
    display: block;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

/* Título */
.titulo-pagina {
    padding: 50px 0 40px;
}

.ultima-noticia .thumb {
    height: 404px;
}

/* Área de notícias */

.noticias-uma-coluna {
    padding-bottom: 50px;
}

.ultima-noticia.ultimas-duas-col {
    margin-bottom: 50px;
}

.titulo-noticia h3 {
    min-height: 81px;
}

.noticias-quatro-col .titulo-noticia h3 {
    font-weight: 700;
    font-size: 16px;
    line-height: 19px;
}

.noticias-quatro-col .titulo-noticia {
    padding: 17px 34px;
}

.noticias-quatro-col .thumb {
    height: 246px;
    background-position: center;
}

.noticias-quatro-col .categoria {
    font-size: 15px;
    margin: 18px 12px;
}

.noticias-quatro-col .titulo-noticia {
    padding: 12px;
}

.noticias-quatro-col .ler-mais a {
    font-size: 14px;
    line-height: 16px;
}

.noticias-quatro-col .titulo-noticia h3 {
    min-height: 76px;
}

.ultima-noticia.noticias-quatro-col {
    margin-bottom: 50px;
}

.noticais-quatro-cols {
    padding-bottom: 50px;
}

@media (max-width:767px) {
    
}
